import arrow from "../imgs/arrow.svg"
import {useState} from "react";
const Registration = () => {
    const [email,setEmail] = useState(undefined);
    const [password,setPassword] = useState(undefined);
    const [name,setName] = useState(undefined);
    const [lastName,setLastName] = useState(undefined)
    const [gender,setGender] = useState(undefined)
    const getGender = (e) => {
        setGender(e.target.value)
        console.log(e.target.value)
    }
    const errors = {
        passwordError : "Password must be 8 or more characters",
        emailError: "Email is incorrect",
        nameError : "Name must contain 1-5 eng. letters", //and Last name
    }
    const error = {
        passwordError : false,
        emailError: false,
        nameError : false,
        lastNameError: false,
    }
    const RegExps = {
        namesReg: /^[A-Za-z]{1,15}$/, //and Last name
        emailReg: /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/,
        passwordReg: /^(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])([a-zA-Z0-9]{8,})$/,
    }


    const changeEmail = (e) => {
        if(RegExps.emailReg.test(e.target.value)) {
            error.emailError = false
            setEmail(e.target.value)
            console.log(error)


        }
        else {
            error.emailError = true
            console.log(error)
            setEmail(undefined)

        }
    }

    const register = () => {
        console.log(error)
    }
    const changePassword = (e) => {
        if(RegExps.passwordReg.test(e.target.value)) {

            error.passwordError = false
            console.log(error)
            setPassword(e.target.value)
        }
        else {
            error.passwordError = true
            console.log(error)
            setPassword(undefined)
        }
    }
    const changeName = (e) => {
        if(RegExps.namesReg.test(e.target.value)) {
            error.nameError = false
            setName(e.target.value)
            console.log(error)
        }
        else {
            error.nameError = true
            console.log(error)
            setName(undefined)
        }
    }

    const changeLastName = (e) => {
        if(RegExps.namesReg.test(e.target.value)) {
            error.lastNameError = false
            setLastName(e.target.value)
            console.log(error)
        }
        else {
            error.lastNameError = true
            setLastName(undefined)
            console.log(error)
        }
    }
    return (
        <div className="container">
            <main className="reg-main">
                <div className="inputs-reg">
                    <div  className="NameAndLastName reg">
                        <span className="your-name">Your Name</span>
                        <input onChange={changeName} id="reg-input" placeholder="First Name" type="text"/>
                        <input onChange={changeLastName} id="reg-input" placeholder="Last Name" type="text"/>
                    </div>

                            <div className="male-or-femeale">
                                <input onChange={getGender} value={"male"} name="maleFemale" type="radio" className="male"  id="checkbox-reg" style={{display: "block"}}/>
                                <span className="male">Male</span>
                                    <input onChange={getGender}  value={"female"} name="maleFemale" className="male" type="radio" id="checkbox-reg checkbox-reg-2"/>
                                <span className={"male"}> Female</span>
                            </div>
                        <div className="loginDetails reg">
                            <span className="login-details">Login details</span>
                            <input id="reg-input" onChange={changeEmail} placeholder="Email" type="email"/>
                            <input onChange={changePassword} id="reg-input" placeholder="Password" type="password"/>
                        </div>

                <span className="p-log-in">Please use 8 or more characters, with at least 1 number and a mixture of
                    uppercase and lowercase letters</span>
                    <button onClick={register} className="btn-join-now">join now <img src={arrow} alt="photo"/>
                    </button>
                </div>

            </main>


        </div>
    )
}

export default Registration
