import {ADD_TO_CART} from "./actions"
import {useSelector} from 'react-redux'

const initialState = {
    cartProducts : []
}
// {
//     ID: "1",
//     name: "chain",
//     price: 200,
//     gender: "man",
//     type: "accessories",
//     description : "",
//     mainFoto: "https://jaxxon.com/static/b8f93c561800af9eabbaa96a3d6ce173/3cd29/CubanFrancoGold2.webp",
//     fotos: ["https://jaxxon.com/static/5e2fc81c8bf5b07acd2d2c06d876e605/3cd29/CubanFrancoGold3.webp","https://jaxxon.com/static/04264e9389d87c359e319a3abd78831f/3cd29/2.5mmFranco_5mmCubanGoldView2.webp","https://jaxxon.com/static/77b2a5b6668391b2cb5df6ce05baa397/3cd29/CubanFrancoGold5.webp","https://jaxxon.com/static/31eab49ca72f383d6197bbbb103869b8/3cd29/CubanFrancoGoldBoxed.webp"]
// }

export const cartReducer = (state = initialState, action) => {
    const getCount = () => {
        state.cartProducts.map(i => {
            let count
         return i.gender == action.payload.gender && i.type == action.payload.type && i.ID == action.payload.ID ? count = 1 : count = 0;
            console.log(count)

        })
    }
    switch (action.type) {
        case ADD_TO_CART:
            return {
                ...state,
                cartProducts: [
                    ...state.cartProducts,
                    {
                        ID: `${action.payload.ID}`,
                        name: `${action.payload.name}`,
                        gender: `${action.payload.gender}`,
                        type: `${action.payload.type}`,
                        price: `${action.payload.price}`,
                        count:  getCount()
                    }
                ]

            }
        default:
            return state
    }
}
