import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import React from "react";
import OrderItem from "../components/OrderItem";
import { useState } from "react";
const Order = (props) => {
  const user = useSelector((state) => state.user);
  let params = useParams()
  const order =  user.users[user.sessionData.email].orders.filter(i =>  i.ID === parseInt(params.ID))[0]
  console.log(order.status);
  const [sum,setSum] = useState(0);

  let keys = []

  const randomKey = () => {

    let key = Math.floor(Math.random() * (90000000 - 10) + 10)

    if (keys.length == 0 || keys.map(element => key !== element)) {
      keys = [...keys, key]
      return key

    } else {
      randomKey()
    }
  }
  const getTotalPrice = () => {
      if(order.orderedProducts.length > 0) {
        let newSum = 0
        order.orderedProducts.map(i => newSum += (i.count * i.price))
        setSum(newSum)
        newSum = 0
        return(5)
      }
      else {
        setSum(0)
      }

  }
  return (
      // <div>status: {order.status === "ordered" ? <LoopIcon/> : order.status === "inDelivery" }</div>
    <div className={"order"}>
      <div className="orderDetails">
        <span>Ordered on: <span>{order.orderedDate}</span></span>
        <span>Will be deilivered to <span>({order.adress.country}) {order.adress.postCode}, {order.adress.city}</span></span>
        <span>Status: <span>{order.status}</span></span>
      </div>
      <div className="wares-cart">
        {order.orderedProducts.map(i => <OrderItem totalPrice={getTotalPrice()} key={randomKey()} color={i.color} size={i.size} ID={i.ID} price={i.price} name={i.name} image={i.image} link={`/products/${i.type}/${i.gender}/${i.ID}`} count={i.count}/>)}
      </div>

    </div>
  )
}
export default Order
