import React from "react";
import Button from '@mui/material/Button';
import NavigateNextIcon from '@mui/icons-material/NavigateNext';
import {Link} from "react-router-dom"
import { useState } from "@types/react";
import { useSelector } from "react-redux";

const OrderPageItem = (props) => {
      let link = `/orders/${props.ID}`
  const [sum,setSum] = useState(0);
  const user = useSelector((state) => state.user);
  const order =  user.users[user.sessionData.email].orders.filter(i =>  i.ID === parseInt(props.ID))[0]

  const getTotalPrice = () => {
    if(order.orderedProducts.length > 0) {
      let newSum = 0
      order.orderedProducts.map(i => newSum += (i.count * i.price))
      setSum(newSum)
      newSum = 0
    }
    else {
      setSum(0)
    }

  }
  return (
    <div className="orderPageItem">
      <span className="orderedDate">Was ordered on <span className={"orderedTime"}>{props.orderedDate} </span></span>
      <span className="oderId">{props.ID ? "ID:" : null} <span className="orderedID">{props.ID }</span></span> <br/>
      <span className={"toOrder"}><Link to={link}><Button variant="contained" className={"toOrder"} endIcon={<NavigateNextIcon />}>
      <span className={"totalPrice"}>Total<span>{props.totalPrice}</span></span>
        To Order
      </Button></Link></span>
    </div>
  )
}

export default OrderPageItem
