import {ADD_TO_CART,CLEAR_CART,CHANGE_QUANITY} from "./actions"
import {useSelector} from 'react-redux'


const initialState = {
    cartProducts : []
}
// {
//     ID: "1",
//     name: "chain",
//     price: 200,
//     gender: "man",
//     type: "accessories",
