import arrow from "../imgs/arrow.svg"
import {useDispatch, useSelector,} from "react-redux";
import {createNewUser} from "../store/user/actions"

import { useCallback, useState } from "react";
import { addToCart, clearCart } from "../store/cart/actions";
const Registration = () => {
  const dispatch = useDispatch()
  const user = useSelector((state) => state.user);

    const [email,setEmail] = useState(undefined);
    const [password,setPassword] = useState(undefined);
    const [name,setName] = useState(undefined);
    const [lastName,setLastName] = useState(undefined)
    const [gender,setGender] = useState(undefined)

  const [loginError,setLoginError] = useState({
    passwordError: true,
    emailError: true,
  })
    const errors = {
        passwordError : "Password must be 8 or more characters",
        emailError: "Email is incorrect",
        nameError : "Name must contain 1-5 eng. letters", //and Last name
    }
   const [error,setError] = useState({
           passwordError: true,
           emailError: true,
           nameError: true,
           lastNameError: true,
           genderError: true,
   })

    const RegExps = {
        namesReg: /^[A-Za-z]{1,15}$/, //and Last name
        emailReg: /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/,
        passwordReg: /^(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])([a-zA-Z0-9]{8,})$/,
    }


    const changeEmail = (e) => {
        if(RegExps.emailReg.test(e.target.value)) {
            setError({
                ...error,
              emailError: false
            })

            setEmail(e.target.value)
            console.log(error)


        }
        else {
            setError({
                ...error,
                emailError: true
            })
            console.log(error)
            setEmail(undefined)

        }
    }
  const changeEmailLogin = (e) => {
    if(Object.keys(user.users).map(i => i === email)) {
      setEmail(e.target.value);


    }
    else {
      setEmail(undefined)

    }
  }
    const getGender = (e) => {
        setGender(e.target.value)
        console.log(e.target.value)
        setError({
            ...error,
            genderError: false
        })
        console.log(error)
    }

    const checkLoginPassword = () => {


      if(user.users[email] !== undefined) {
        if(user.users[email].pin === password) {
          setLoginError(
            {
              ...loginError,
              passwordError: false,
            })
          console.log("password is correct");

        } else {
          console.log("password is incorrect");
          setLoginError(
            {
              ...loginError,
              passwordError: true,
            })
        }
      }

    }
   const checkLoginEmail = () => {
     if(user.users[email] !== undefined) {
       console.log("email is correct =>>> ")
       setLoginError(
         {
           ...loginError,
           emailError: false,
         })
     } else {
       console.log(loginError);
       setLoginError(
         {
           ...loginError,
           emailError: true,
         })
       console.log("email or password are incorrect");
     }
    }
    const logIn = () => {

      checkLoginEmail()
      checkLoginPassword()

    }
    const register = () => {
      if(Object.keys(error).map(i => error[i]).includes(true)) {
            console.log(true)
          console.log(user.users);

        } else {
          // pin,email,gender,firsName,lastName
          if(!Object.keys(user.users).map(i => i === name).includes(true)) {
            dispatch(createNewUser(password,email,gender,name,lastName))
            console.log(user.users);
          }

        }
    }
  const changePasswordLogin = (e) => {
    if(e.target.value !== undefined) {
      setPassword(e.target.value)
    }
    else {
      setPassword(undefined)
    }
  }
    const changePassword = (e) => {
        if(RegExps.passwordReg.test(e.target.value)) {
            setError({
                ...error,
                passwordError: false
            })
            console.log(error)
            setPassword(e.target.value)
        }
        else {
            setError({
                ...error,
                passwordError: true
            })
            console.log(error)
            setPassword(undefined)
        }
    }
    const changeName = (e) => {
        if(RegExps.namesReg.test(e.target.value)) {
           setError({
               ...error,
               nameError: false
           })
            setName(e.target.value)
            console.log(error)
        }
        else {
            setError({
                ...error,
                nameError: true
            })
            console.log(error)
            setName(undefined)
        }
    }

    const changeLastName = (e) => {
        if(RegExps.namesReg.test(e.target.value)) {
            setError({
                ...error,
                lastNameError: false
            })
            setLastName(e.target.value)
            console.log(error)
        }
        else {
            setError({
                ...error,
                lastNameError: true
            })
            setLastName(undefined)
            console.log(error)
        }
    }
    return (
        <div className="container">
            <main className="reg-main">
              {user.loggedIn == false ?
                <div className="inputs-reg">
                <div  className="NameAndLastName reg">
                  <span className="your-name">Your Name</span>
                  <input onChange={changeName} id="reg-input" placeholder="First Name" type="text"/>
                  <input onChange={changeLastName} id="reg-input" placeholder="Last Name" type="text"/>
                </div>

                <div className="male-or-femeale">
                  <input onChange={getGender} value={"male"} name="maleFemale" type="radio" className="male"  id="checkbox-reg" style={{display: "block"}}/>
                  <span className="male">Male</span>
                  <input onChange={getGender}  value={"female"} name="maleFemale" className="male" type="radio" id="checkbox-reg checkbox-reg-2"/>
                  <span className={"male"}> Female</span>
                </div>
                <div className="loginDetails reg">
                  <span className="login-details">Login details</span>
                  <input id="reg-input" onChange={changeEmail} placeholder="Email" type="email"/>
                  <input onChange={changePassword} id="reg-input" placeholder="Password" type="password"/>
                </div>

                <span className="p-log-in">Please use 8 or more characters, with at least 1 number and a mixture of
                    uppercase and lowercase letters</span>
                <button onClick={register} className="btn-join-now">join now <img src={arrow} alt="photo"/>
                </button>
              </div> :
                <div className="inputs-reg">

                  <div className="loginDetails reg">
                    <span className="login-details">Login</span>
                    <input id="reg-input" onChange={changeEmailLogin} placeholder="Email" type="email"/>
                    <input onChange={changePasswordLogin} id="reg-input" placeholder="Password" type="password"/>
                  </div>

                  <span className="p-log-in">Please use 8 or more characters, with at least 1 number and a mixture of
                    uppercase and lowercase letters</span>
                  <button onClick={logIn} className="btn-join-now">login <img src={arrow} alt="photo"/>
                  </button>
                </div>}

            </main>


        </div>
    )
}

export default Registration
