import CartItem from "../components/CartItem"
import {clearCart} from "../store/cart/actions"
import {useDispatch, useSelector,} from "react-redux";
import React, { useState, useEffect } from 'react';


const CartPage = (props) => {
    const dispatch = useDispatch()
    const [sum,setSum] = useState(0);
    let cart = []
    const cartProducts = useSelector((state) => state.cartProducts.cartProducts);
    console.log(cartProducts)
    const calculacteSum = () => {
        if(cartProducts.length > 0) {
            let dum = 0
            cartProducts.map(i => dum += (i.count * i.price))
            setSum(dum)
            dum = 0
            console.log(sum)
        }
        else {
            setSum(0)
        }
    }
    useEffect(() => {
        calculacteSum()
    },[cartProducts]);
    /*
    * ITEM
    * {
    *
    * name: t-shirt | string,
    * price: 50 | init,
    * type: accessories | string,
    * gender: man
    * id: id
    * mainFoto : link to your image
    * fotos: [ link to your image , link to your image , link to your image, ]
    *
    * }
    *
    * */

    return (
        <>  
            <main class="main-cart">
                <div class="flex-cart">
                    <div class="wares-cart">
                        {cartProducts.map(i => <CartItem   calculacteSum color={i.color} size={i.size} ID={i.ID} price={i.price} name={i.name} image={i.image} link={`/products/${i.type}/${i.gender}/${i.ID}`} count={i.count}/>)}
                        <div className="btns-cart">
                            {cartProducts.length > 0 ?  <button onClick={() => dispatch(clearCart())} className="clear-shopping-cart">clear shopping cart</button> : null}
                        </div>
                    </div>
                </div>
                <div class="inputs-shipping-adress">

                    <input type="text" id="inputs-cart" placeholder="Bangladesh"/>
                        <input type="text" id="inputs-cart" placeholder="State"/>
                            <input type="number" id="inputs-cart" placeholder="Postcode / Zip"/>
                                <button type="submit" id="get-a-quote"><span class="get-a-quote-text">get a quote</span></button>
                                <div class="proceed-to-checkout">
                                    <p class="sub-total-p">sub total<span class="sub-total">${sum}</span></p>
                                    <p class="grand-total-p">Grand total<span class="grand-total">${sum}</span></p>
                                    <div class="line-shipping"></div>
                                    <button class="procced-btn">proceed to checkout</button>

                                </div>
                </div>


        <div/>
</main></>
    )
}
export default CartPage
