import {useParams} from "react-router-dom"
import {useSelector} from 'react-redux'
import {state} from "../store"
import { useNavigate } from 'react-router-dom';
import ProductItem from "./ProductItem"
import * as React from 'react';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Slide from '@mui/material/Slide';

const SearchPage = (props) => {

    let params = useParams()
    const search = useSelector((state) => state.search.searchLine);
    const products = useSelector((state) => state.products.products);
    const genders = ["man","woman","kids"]
    const types = ["t-shirts","denim","accessories","bags"]
    console.log(params)
//     <ProductItem name={i.name} image={i.mainFoto} link={`/products/}`} price={i.price}/>
// // products[params.type].men.map(i => <ProductItem name={i.name} image={i.mainFoto} link={`/products/}`} price={i.price}/>)
//     Object.keys(products[params.type]).map(item => products[params.type][item].map(i => i.name))
   let ifPageExist = () => {
        if(types.includes(params.type) && genders.includes(params.gender)) {
           if(products[params.type][params.gender].length === 0) {
               // return <span>we don`t have products here yet</span>
               return <div>
                   <Button variant="outlined" onClick={handleClickOpen}>
                       Slide in alert dialog
                   </Button>
                   <Dialog
                       open={open}
                       TransitionComponent={Transition}
                       keepMounted
                       onClose={handleClose}
                       aria-describedby="alert-dialog-slide-description"
                   >
                       <DialogTitle>{"Use Google's location service?"}</DialogTitle>
                       <DialogContent>
                           <DialogContentText id="alert-dialog-slide-description">
                               Let Google help apps determine location. This means sending anonymous
                               location data to Google, even when no apps are running.
                           </DialogContentText>
                       </DialogContent>
                       <DialogActions>
                           <Button onClick={handleClose}>Disagree</Button>
                           <Button onClick={handleClose}>Agree</Button>
                       </DialogActions>
                   </Dialog>
               </div>
           }
           else {
               console.log(products[params.type][params.gender])
               return products[params.type][params.gender].map(i => <ProductItem name={i.name} image={i.mainFoto} link={`/products/}`} price={i.price}/>)
           }
        }
        else {
            return <span>no pages found</span>

       }
   }
    return (
        <div className={"cards product-cards"}>
            {    props.isFiltered == false && props.isHome == true && search === "" ? <span>render 6 products</span> :
                props.isFiltered == false && props.isHome == false && search === "" && params.itemID !== undefined ? <span>product page</span>
                : props.isFiltered == false && props.isHome == false && search === "" && params.type !== undefined && params.gender === undefined ?
                        Object.keys(products[params.type]).map(item => products[params.type][item].map(i => <ProductItem name={i.name} image={i.mainFoto} link={`/products/}`} price={i.price}/>)) :
                                props.isFiltered == false && props.isHome == false && search === "" && params.type === undefined ?  <span>all</span>  :
                                    props.isFiltered == false && props.isHome == false && search === "" && params.itemID == undefined && params.gender !== undefined ? ifPageExist() :
                                        props.isFiltered == true ? <span>filter name: {search}</span> : null
            }
            {

            }
            {/*search == "" && params.type === undefined ? <span>ALL products</span>*/}
            {/*: params.gender === !undefined && search == "" ? <span>PRODUCT</span>*/}
            {/*: <span>PRODUCT </span>*/}
        </div>
    )
}

export default SearchPage
